import axios from 'axios'

export const fetchData = () => {
    return axios.get("https://covid19.mathdro.id/api")
    .then(response => {
        const {data: {confirmed, recovered, deaths, lastUpdate}} = response
        const modifieddata = {confirmed, recovered, deaths, lastUpdate}
        return modifieddata
    })
    .catch(error => {
        console.log("Errooorrrrrr",error)
    })
}

export const fetchDailyData = () => {
    return axios.get("https://covid19.mathdro.id/api/daily")
    .then(response => {
        const {data} = response
        const modifiedData = data.map((dailydata) => ({
            confirmed: dailydata.confirmed.total,
            deaths: dailydata.deaths.total,
            date: dailydata.reportDate
        }))
        return modifiedData
    })
    .catch(error => {
        console.log("Error", error)
    })
}
