import React, {useState, useEffect} from 'react'
import styles from './CountryPicker.module.css'
import {NativeSelect, FormControl} from '@material-ui/core'
import {fetchingCountries} from '../../api/index.js'
import axios from 'axios'

const url = "https://covid19.mathdro.id/api"
function CountryPicker(props) {
    const[countries, setCountries] = useState([])

    useEffect(()=>{
        //setCountries(props.countries)
        axios.get(`${url}/countries`)
        .then(response => {
            const {data:{countries}} = response
            const countriesResult = countries.map((country) => (country.name))
            setCountries(countriesResult)
        })
        .catch(error => {
            console.log("Error", error)
        })
        console.log("CountryPicker", countries)
    }, [countries])


    return (
        <FormControl className = {styles.formControl}>
            <NativeSelect defaultValue = "" onChange = {(event) => props.handleCountryChange(event.target.value)}>
                <option value="global">Global</option>
                {countries.map((country, i) => (<option key={i} value={country}> {country} </option>))}
            </NativeSelect>
        </FormControl>
    )
}

export default CountryPicker
